    function normalizeYearsMonths($num) {
        $years = floor($num);
        $months = round(($num - $years) * 100); // Always between 1-11
        // Remove trailing zero from months if present
        $months_normal = rtrim(rtrim((string)$months, "0"), ".");
        return $years . '.' . $months_normal;
    }

